import Vue from 'vue'
import App from './App.vue'
import Vuetify from 'vuetify/lib' // Versión de Vuetify para Vue 3

Vue.use(Vuetify);

const vuetify = new Vuetify({
  theme: {
    themes: {
      light: {
        primary: '#1976D2',  // Cambiar este color al deseado
        secondary: '#424242',
        accent: '#82B1FF',
        error: '#FF5252',
        info: '#2196F3',
        success: '#4CAF50',
        warning: '#FFC107'
      }
    }
  }
});

new Vue({
  vuetify,
  render: h => h(App)
}).$mount('#app');
